import React, { Component } from 'react'

import classNames from 'classnames'


export default class LoginForm extends Component {
    constructor(props){
        super(props)

        this.state = {
            active:"login",
            firstrName:"",
            lastName:"",
            login:"",
            password:"",
            onLogin:props.onLogin,
            onRegister:props.onRegister

        }
    }

    onChangeHandler = (e) => {
        let name = e.target.name
        let value = e.target.value
        this.setState({[name]:value})
    }

    onSubmitLogin= (e) => {
        this.state.login(e,this.state.login,this.state.password)
    }

    onSubmitRegister = (e) => {
        this.state.onRegister(
            e,
            this.state.firstrName,
            this.state.lastName,
            this.state.login,
            this.state.password
        )
    }
  render() {
    return (
      <div className='row justify-content-center'>
        <div className='"col-4'>
            <ul className='nav nav-pills nav-justified mb-3'>
                <li className='nav-items' role='presentation'>
                    <button className={classNames("nav-link",this.state.active === "login" ? "active":"")} id='tab-login'>Login</button>
                </li>
                <li className='nav-items' role='presentation'>
                    <button className={classNames("nav-link",this.state.active === "register" ? "active":"")} id='tab-register'>Register</button>
                </li>
            </ul>
            <div className='tab-content'>
                <div className={classNames("tab-pane","fade",this.state.active === "login" ? "show active":"")} id='tab-login'>
                    hi
                </div>

            </div>
        </div>
      </div>
    )
  }
}
